    prefixed_cmd = _HybridToPrefixedCommand(
        name=str(cmd.sub_cmd_name) if cmd.is_subcommand else str(cmd.name),
        aliases=list(_values_wrapper(cmd.sub_cmd_name.to_locale_dict()))
        if cmd.is_subcommand
